<?php	if($this->isNotActivated()):?>	<p>Управление объявлениями доступно только активированным пользователям.</p><?php	else:?>		<?php if(!empty($offerList) and $offerList->count()):	?>	<form name="myofferslist" action="" method="post" id="editlist_form" class="editlist offerlist">	<?php	$categories = ORM::factory('category')->in('id',$offerList->getValues('category_id'))->find_all()->as_id_array();		$types = ORM::factory('type')->find_all()->select_list(NULL, 'intention_title');		$regions = ORM::factory('region')->find_all()->select_list();			$offerIDs = array_keys($offerList->select_list());				if(count($offerIDs)) $pictures = ORM::factory('picture')->find_all_for('offer', $offerIDs);		foreach($offerList as $offer):			if($offer->is_expired and $offer->status == 'enabled') $offer->status = 'expired';?>						<div class="ofr <?php 				if($offer->is_premium):					echo ' premium'; 				elseif($offer->is_marked):					echo ' or';						endif;								echo ' ' .$offer->status;								?>" id="o<?=$offer->id?>"><i class="ct"><i></i><b></b></i>				<table >					<tr><td class="ch"><input type="checkbox" name="id[]" value="<?=form::value($offer->id)?>" /></td>						<td class="p"><?php if(isset($pictures[$offer->id])):?><a href="<?=$offer->url?>"><?php echo $pictures[$offer->id]->f('thumb','html')?></a><?php endif?></td>						<td class="d">							<h3><a href="<?=$offer->url?>"><?=$offer->title?></a></h3>							<p><?=$offer->short_description?></p>						</td>						<td class="i">	                        <?=$offer->price_html_list?>							<div class="c"><a href="<?=$categories[$offer->category_id]->url?>"><?=$categories[$offer->category_id]->title?></a></div>							<div class="c"><?=$regions[$offer->region_id]?></div>						</td>						<td class="s">							<b>Добавлено</b>							<p><?=date::getSimple($offer->added)?></p><?php	switch($offer->status):			case 'enabled':?>														<i>Актуально до</i>							<p><?=date::getSimple($offer->expiration)?></p><?php		break;			case 'expired':?>							<i>Просрочено</i>							<p><?=date::getSimple($offer->expiration)?></p><?php		break;			case 'banned':?>							<i>Заблокировано</i><?php						if(!$offer->checked):?>							<p>Проверяется модератором</p><?php						else:?>								<p>&nbsp;</p>					<?php						endif;?><?php		break;			default:?>							<i><?=Lib::config('app.offer_status', $offer->status)?></i>							<p>&nbsp;</p><?php		break;		endswitch;?>													</td>					</tr>				</table>				<div class="actions"><?php	if($mode != 'deleted'):?>						<div class="but">						<a href="<?=$offer->url_edit?>" class="edit">Редактировать</a>						<a href="<?=$offer->url_expiration?>" class="modal list_expiration<?php if($offer->status != 'enabled' and $offer->status != 'expired') echo ' dn'?>">Продлить</a>						<a href="<?=$offer->url_enable?>" class="list_enable<?php if( $offer->status != 'disabled') echo ' dn'?>">Включить</a>						<a href="<?=$offer->url_disable?>" class="list_disable<?php if( $offer->status != 'enabled') echo ' dn'?>">Отключить</a>						<a href="<?=$offer->url_delete?>" class="delete modal">Удалить</a>					</div><?php	else:?>					<div class="but">						<a href="<?=$offer->url_undelete?>" class="list_undelete">Восстановить</a><?php		if($this->isModerator()):?>												<a href="<?=$offer->url_remove?>" class="remove modal">Удалить окончательно</a><?php		endif?>					</div>		<?php	endif?>				</div>				<div class="clb"></div>			</div><?php	endforeach;?>		<div class="buttons">			<div class="service_links"><a href="#" id="select_all">Выделить все</a> <a href="#" id="deselect_all">Отменить выделение</a></div>			<div class="note">Действия над выделенными объявлениями:</div><?php	echo @$form_messages?>			<?php	if($mode != 'deleted'):?>						<?php		if($mode != 'enabled'):?>							<input type="submit" name="enable_selected" value="Включить" class="btn btn-outline-info" /><?php		endif;			if($mode != 'disabled'):?>						<input type="submit" name="disable_selected" value="Выключить" class="btn btn-outline-info" /><?php		endif;?>			<input type="submit" name="delete_selected" value="Удалить" class="btn btn-outline-info" /><?php	else:?>			<input type="submit" name="recover_selected" value="Восстановить" /><?php		if($this->isModerator()):?>						<input type="submit" name="remove_selected" value="Удалить" /><?php		endif;?><?php	endif?>			<span id="ajaxstatus">Отправка данных...</span>		</div>				<?php echo $pagination;?>				<div id="modalBox" class="jqmWindow"><a href="#" class="jqmClose x">закрыть</a><div class="jqmContent"></div></div>	</form><?php else:?>	<p class="tmar"><?=!empty($notfound)?$notfound:'Нет объявлений'?></p>		<?php endif; // offersList Count?><?php endif; // isNotActivated?> 